import React, { useContext } from 'react';
import Copy from 'clipboard-copy';
import MyContext from '../context/MyContext';
import shareIcon from '../images/shareIcon.svg';
import '../styles/RecipeCardsDone.css';

function RecipeCardsDone() {
  const {
    copied,
    setCopied,
    cardType,
    setCardType,
    cardId,
    setCardId,
    done,
  } = useContext(MyContext);

  const doneRecipes = done;
  const LIMIT = 2;

  console.log(doneRecipes);

  return (
    doneRecipes.map((card, index) => (
      <div
        key={ card.id }
        data-testid={ `${index}-recipe-card` }
        onClick={ () => {
          setRecipe(item[`id${type.palavra}`]);
          redirectToDetails(item[`id${type.palavra}`]);
        } }
        onKeyPress={ () => console.log('clicou') }
        role="button"
        tabIndex={ card.index }
        className="cardsDone"
      >
        <div className="cardsDoneImg">
          <img
            src={ card.image }
            alt={ card.name }
            data-testid={ `${index}-horizontal-image` }
          />
        </div>
        <div className="testes">
          <button
            className="btn"
            type="button"
            data-testid={ `${index}-horizontal-share-btn` }
            onClick={ () => {
              Copy(`http://localhost:3000/${card.type}s/${card.id}`);
              setCopied(true);
              setCardType(card.type);
              setCardId(card.id);
            } }
            src={ shareIcon }
          >
            {copied && cardType === card.type && cardId === card.id && 'Link copiado!'}
            <span />
          </button>
          <p
            data-testid={ `${index}-horizontal-top-text` }
          >
            {(card.type === 'comida'
              ? `${card.area} - ${card.category}`
              : card.alcoholicOrNot)}
          </p>
          <p
            data-testid={ `${index}-horizontal-name` }
          >
            {card.name}
          </p>
          <p
            data-testid={ `${index}-horizontal-done-date` }
          >
            {`Feita em: ${card.doneDate}`}
          </p>
          <div>
            {card.tags.map((tag, indexTag) => {
              if (indexTag < LIMIT) {
                return (
                  <span
                    data-testid={ `${index}-${tag}-horizontal-tag` }
                    className="tags"
                    key={ indexTag }
                  >
                    {tag}
                  </span>
                );
              }
              return null;
            })}
          </div>
        </div>
      </div>
    ))
  );
}

export default RecipeCardsDone;
